package com.example.demofetchtypejpa.controller;import com.example.demofetchtypejpa.entity.ProductEntity;import com.example.demofetchtypejpa.entity.ProductInventoryEntity;import com.example.demofetchtypejpa.entity.ProductTypeEntity;import com.example.demofetchtypejpa.service.ProductService;import lombok.AllArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import java.util.List;import java.util.UUID;@RestController@AllArgsConstructor@Slf4jpublic class ProductController {    private final ProductService productService;    @GetMapping("/products")    public ResponseEntity<List<ProductEntity>> getProducts() {        return ResponseEntity.ok(productService.getProducts());    }    @GetMapping("/product-types")    public ResponseEntity<List<ProductTypeEntity>> getProductTypes() {        return ResponseEntity.ok(productService.getProductTypes());    }    @GetMapping("/product-inventory")    public ResponseEntity<ProductInventoryEntity> getProductInventories() {        return ResponseEntity.ok(productService.getProductInventories(UUID.fromString("21a11de1-9d44-4489-b899-e2ff29063cf4")));    }}