package com.example.demofetchtypejpa.entity;import jakarta.persistence.*;import lombok.Data;import java.util.UUID;@Data@Entity(name = "productInventory")@Table(name = "productInventory")public class ProductInventoryEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id")    private UUID id;    @OneToOne(fetch = FetchType.LAZY)    private ProductEntity product;    @Column(name = "quantity")    private Integer quantity;}