package com.example.demofetchtypejpa.service;import com.example.demofetchtypejpa.entity.ProductEntity;import com.example.demofetchtypejpa.entity.ProductInventoryEntity;import com.example.demofetchtypejpa.entity.ProductTypeEntity;import com.example.demofetchtypejpa.repository.ProductInventoryRepository;import com.example.demofetchtypejpa.repository.ProductRepository;import com.example.demofetchtypejpa.repository.ProductTypeRepository;import lombok.AllArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Service;import java.util.List;import java.util.UUID;@Service@AllArgsConstructor@Slf4jpublic class ProductService {    private final ProductRepository productRepository;    private final ProductTypeRepository productTypeRepository;    private final ProductInventoryRepository productInventoryRepository;    public List<ProductEntity> getProducts() {        List<ProductEntity> products = productRepository.findAll();        log.info("Product : {}", products);        return products;    }    public List<ProductTypeEntity> getProductTypes() {        return productTypeRepository.findAll();    }    public ProductInventoryEntity getProductInventories(UUID productId) {        log.info("productId : {}", productId);        ProductInventoryEntity productInventory = productInventoryRepository.findByProductId(productId);        log.info("ProductInventoryEntity : {} ", productInventory);        return productInventory;    }}